import {useState, useEffect} from 'react';

const useForm = () => {
    const [value, setValues] = useState ({
        name: '',
        email: '',
        message: ''
    })

    const [errors, setErrors] = useState({})


    const handleChange = e => {
        const {name, value} = e.target
        setValues({
            ...values,
            [name]: value
        })
    }

    return {handleChange, values}

}

export default useForm
