import React from 'react';
import style from 'styled-components';
// import { Modal } from './Modal';

const Section = style.section`
    background: url(${({ image }) => image && image}) center;
    height: 100vh;
    display: flex;
    justify-content: flex-start;
    align-items: flex-end;
    margin-top: -60px;
    `;

const Container = style.div`
    color: #fff;
    padding: 1rem;
    backdrop-filter: blur(5px);
    background-color: rgba(0, 0, 0, 0.4);
    margin: 15rem 20rem;
    border-radius: 10px;
    h1{
        font-size: clamp(2rem, 8vw, 5rem);
        margin-top: 0.1rem;
        margin-bottom: 0.1rem;
    }
    // button{
    //     font-size: clamp(0.8rem, 4vw, 1.2rem);
    //     padding: 0.8rem 2rem;
    //     color: #000;
    //     background: #F01F13;
    //     background: linear-gradient(to right, #D94E18, #F01F13);
    //     border: none;
    //     border-radius 4px;
    //     cursor: pointer;
    //     outline: none;
    // }
    `;

const Footer = style.footer`

    align-items: center;
    justify-content: center;
    text-align: center;
    padding-right: 1rem;

    img {
        height: 50px;
        width: 50px;
        cursor: pointer;
    }
    `;

export const Hero = ({ image }) => {

    // const [showModal, setShowModal] = useState(false)

    // const openModal = () => {
    //     setShowModal(prev => !prev);
    // }

    return (
        <Section image={image}>
            <Container>
                <h1>Hello, I'm</h1>
                <h1>Noel Luna</h1>
                {/* <button onClick={openModal}>Contact</button> */}
                {/* <Modal showModal={showModal} setShowModal={setShowModal} /> */}
            </Container>
            <Footer>
                <img href="/LinkedIn" src={require("../logos/linkedin.png")} />
                <img href="/Github" src={require("../logos/github.png")} /> 
            </Footer>
        </Section>
    );
};

export default Hero;
