import emailjs from 'emailjs-com';

export default function validateInfo(values) {
    let errors = {}

    //Name
    if(!values.name.trim()) {
        errors.name = "Name Required"
    }

    //Email
    if(!values.user_email){
        errors.user_email = "Email Required"
    } else if (!/\S+@\S+\.\S+/.test(values.user_email)) {
        errors.user_email = "Email address is invalid"
    }

    //Message
    if(!values.message.trim()) {
        errors.message = "Message Required"
    }

    if(!errors) {
      emailjs.sendForm('service_f6m8b5b', 'template_cbi1mm7', values, 'user_EVRTefLUNOWTEEy4t8sBW').then(res=>{
        console.log(res);
      }).catch(err=> console.log(err));

      alert("Message Sent!")
    }

    return errors;
}
